<template>
  <div style="width:400px">
    <st-image :src="imageUrl"></st-image>
    <st-button @click="delImageUrl" class="mg-r16">取消</st-button>
    <st-button type="primary" @click="uploadImage">加载图片</st-button>
  </div>
</template>

<script>
export default {
  name: 'StImageDemo',
  data() {
    return {
      imageUrl: '',
      timer: ''
    }
  },
  methods: {
    uploadImage() {
      this.timer = setTimeout(() => {
        clearTimeout(this.timer)
        this.imageUrl =
          'data:image/png;base64,'
      }, 3000)
    },
    delImageUrl() {
      this.imageUrl = ''
    }
  }
}
</script>
